import React from "react";
import data from "./countries.json";
const Ulkeler = () => {
  const stil = {
    textAlign: "center",
    backgroundColor: "lightblue",
  };
  return (
    <div className="tblContainer">
      <select name="" id="" defaultValue="ulke">
        <option disabled value="ulke">
          Bir ülke seçin
        </option>
        {data.map((country) => (
          <option key={country.code} value={country.code}>
            {country.name}
          </option>
        ))}
      </select>
      <table>
        <thead>
          <tr>
            <th>Sıra No</th>
            <th>Ülke Adı</th>
            <th>Ülke Kodu</th>
          </tr>
        </thead>
        <tbody>
          {data
            .filter((item) => item.name.startsWith("A"))
            .map((country, index) => (
              <tr
                key={index}
                style={
                  index % 2 == 0
                    ? stil
                    : { ...stil, backgroundColor: "lightgray" }
                }
              >
                <td></td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default Ulkeler;
